import { useState } from "react";

function RoomInformationCard(props) {
// Komponenten tar emot props.

const [chosenRooms, setChosenRooms] = useState([]);
    
    function handleCheckedChange(e) {
    // Funktionen tar emot ett event.
    
      let updatedCheckedRooms = [...chosenRooms];
      if (e.target.checked) {
        updatedCheckedRooms = [...chosenRooms, e.target.value];
      } else {
        updatedCheckedRooms.splice(chosenRooms.indexOf(e.target.value), 1);
      }
      setChosenRooms(updatedCheckedRooms);
      props.setChosenHotel(chosenRooms);
    }
  return (

    <div className="RoomInformationCard">
        <div key={props.id}>
            <ol>
                <li>
                    <ul key={props.rooms[0].roomID}>
                        <li>{props.rooms[0].roomName}</li>
                        <li>Room for {props.rooms[0].guests} guest(s)</li>
                        <label>Choose room <input type="checkbox" value={[props.rooms[0].roomID, props.id]} onChange={handleCheckedChange}/></label>
                    </ul>
                </li>

                <li>
                    <ul key={props.rooms[1].roomID}>
                        <li>{props.rooms[1].roomName}</li>
                        <li>Room for {props.rooms[1].guests} guests</li>
                        <label>Choose room <input type="checkbox" value={[props.rooms[1].roomID, props.id]} onChange={handleCheckedChange}/></label>
                    </ul>
                </li>

                <li>
                    <ul key={props.rooms[2].roomID}>
                        <li>{props.rooms[2].roomName}</li>
                        <li>Room for {props.rooms[2].guests} guests</li>
                        <label>Choose room <input type="checkbox" value={props.rooms[2].roomID + props.id} onChange={handleCheckedChange}/></label>
                    </ul>
                </li>
            </ol>
        </div>
    </div>
  )
}

export default RoomInformationCard;
// Exporterar komponenten som "RoomInformationCard".
