import './App.css';
// Importerar CSS.

import { useState } from "react";

import NavBar from "./components/NavBar.js";
// Importerar komponenten "NavBar".

import SearchFilter from "./components/SearchFilter";
// Importerar komponenten "SearchFilter".

import InformationCard from "./components/InformationCard.js";
// Importerar komponenten "InformationCard".

import hotelpool from "./images/hotel_pool.jpg";
// Importerar två bilder.

//import hotels from "./hotels.json";

const hotels = [
  {"hotelName": "Hotel Jones", "destination": "New York City, NY, USA", "description": "Cheap and near the sea.", "image":".png", "price":"199"}
];

function App() {

  const [searchDestination, setSearchDestination] = useState("");
  const [searchCheckInDate, setSearchCheckInDate] = useState(new Date());
  const [searchCheckOutDate, setSearchCheckOutDate] = useState(new Date());
  const [searchGuests, setSearchGuests] = useState(0);

  return (
    <div className="App">
      <NavBar/>

      <div className="Intro">
        <img src={hotelpool}/>
        <div className="IntroContent">
          <p>Your home away from home</p>
          <p className="IntroSlogan">Where Comfort and Affordability meet</p>
          <a href="#" className="IntroButton">Explore now</a>
        </div>
      </div>

      <SearchFilter searchDestinationChange={event => setSearchDestination(event.target.value)} searchDestination={searchDestination}
      searchCheckInDateChange={event => setSearchCheckInDate(event.target.value)} searchCheckInDate 
      searchCheckOutDateChange={event => setSearchCheckOutDate(event.target.value)}
      searchGuestsChange={event => setSearchGuests(event.target.value)} 

      {...hotels.filter(function(results) {

        if (searchDestination === "" || searchCheckInDate === "" || searchCheckOutDate === "" || searchGuests === "") {

          return null;

        } else if (results.destination.toLowerCase().includes(searchDestination.toLowerCase())) {

          return results;

        }
      }).map(({hotelName, destination, description, image, price}) => (

        <InformationCard hotelName={hotels.hotelName} destination={hotels.destination} description={hotels.description} image={hotels.image} price={hotels.price}/>

      ))}

      />
    </div>
  );
}

export default App;
