import { useState } from 'react';

function InformationCard(props) {

  const [chosenHotel, setChosenHotel] = useState(null);

  function chooseHotel(identifier) {

    setChosenHotel(props.hotelName[identifier]);
    console.log(props.chosenHotel);
  }
    
  return (

    <div className="InformationCard">
        <h2>{props.hotelName}</h2>
        <p>{props.destination}</p>
        <p>{props.description}</p>
        <img src={props.image}/>
        <p>${props.price} per night</p>
        <button key={props.id} onClick={() => props.chooseHotel(props.id)}>Choose</button>
    </div>
  )
}

export default InformationCard;
// Exporterar komponenten som "InformationCard".
